sect1.programlisting:
(* freq.ml: basic implementation *)
open Core.Std
let build_counts () = In_channel.fold_lines stdin ~init:[] ~f:(fun counts line -> let count = match List.Assoc.find counts line with | None -> 0 | Some x -> x in List.Assoc.add counts line (count + 1) )
let () = build_counts () |> List.sort ~cmp:(fun (_,x) (_,y) -> compare y x) |> (fun l -> List.take l 10) |> List.iter ~f:(fun (line,count) -> printf "%3d: %s\n" count line)

(* counter.ml: first version *)
open Core.Std
let touch t s = let count = match List.Assoc.find t s with | None -> 0 | Some x -> x in List.Assoc.add t s (count + 1)

(* freq.ml: using Counter *) open Core.Std
let build_counts () = In_channel.fold_lines stdin ~init:[] ~f:Counter.touch
let () = build_counts () |> List.sort ~cmp:(fun (_,x) (_,y) -> compare y x) |> (fun l -> List.take l 10) |> List.iter ~f:(fun (line,count) -> printf "%3d: %s\n" count line)

(* filename: freq.ml *) open Core.Std
let build_counts () = In_channel.fold_lines stdin ~init:Counter.empty ~f:Counter.touch
let () = build_counts () |> Counter.to_list |> List.sort ~cmp:(fun (_,x) (_,y) -> Int.descending x y) |> (fun counts -> List.take counts 10) |> List.iter ~f:(fun (line,count) -> printf "%3d: %s\n" count line)

type median = | Median of string
              | Before_and_after of string * string

let median t =
  let sorted_strings = List.sort (Map.to_alist t)
      ~cmp:(fun (_,x) (_,y) -> Int.descending x y)
  in
  let len = List.length sorted_strings in
  if len = 0 then failwith "median: empty frequency count";
  let nth n = fst (List.nth_exn sorted_strings n) in
  if len mod 2 = 1
  then Median (nth (len/2))
  else Before_and_after (nth (len/2 - 1), nth (len/2));;

